definition.script:
pipeline
{
    agent {node('built-in')}
    stages
    {
        stage('continuous delivery')
        {
            steps
            {
                git 'https://github.com/keshavr21/maven_test.git'
            }
        }
        stage('continuous build')
        {
            steps
            {
                sh '/opt/maven/bin/mvn clean package'
            }
        }
        stage('continuous deploy')
        {
            steps
            {
               deploy adapters: [tomcat9(credentialsId: '15d8b3b2-1981-4a3a-ae9b-c864f9fa8d16', path: '', url: 'http://172.31.39.150:8080/')], contextPath: 'dec', war: '**/*.war'    
            }
        }
        stage('continuous testing')
        {
            steps
            {
                git 'https://github.com/keshavr21/test_cases.git'
                sh 'java -jar testing.jar $WORKSPACE/webapp/target/webapp.war'
            }
        }
        stage('Email')
        {
            steps
            {
            emailext attachLog: true, body: 'pipeline', subject: 'dec pipeline', to: 'parthasarathi.parandhaman@gmail.com'        
            }
        }
    }
}
